import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Turma, Aluno } from '../types';
import { apiService } from '../services/api';

const TurmaDetalhePage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [turma, setTurma] = useState<Turma | null>(null);
  const [alunos, setAlunos] = useState<Aluno[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isUsingMockData, setIsUsingMockData] = useState(false);

  useEffect(() => {
    const fetchTurmaDetails = async () => {
      if (!id) return;

      try {
        setLoading(true);
        setError(null);

        // Capturar warnings do console para detectar uso de dados mock
        const originalWarn = console.warn;
        let mockDetected = false;
        console.warn = (...args) => {
          if (args[0]?.includes('API n√£o dispon√≠vel')) {
            mockDetected = true;
          }
          originalWarn(...args);
        };

        const turmaId = parseInt(id);

        // Buscar dados da turma e alunos inscritos
        const [turmaData, alunosData] = await Promise.all([
          apiService.getTurma(turmaId),
          apiService.getAlunosPorTurma(turmaId)
        ]);

        setTurma(turmaData);
        setAlunos(alunosData);
        setIsUsingMockData(mockDetected);

        // Restaurar console.warn
        console.warn = originalWarn;

      } catch (err) {
        setError('Erro ao carregar detalhes da turma. Verifique se a API est√° rodando.');
        console.error('Erro ao buscar detalhes da turma:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTurmaDetails();
  }, [id]);

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '200px',
        fontSize: '18px',
        color: '#6c757d'
      }}>
        <div>Carregando detalhes da turma...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ padding: '20px' }}>
        <button
          onClick={() => navigate('/turmas')}
          style={{
            marginBottom: '20px',
            padding: '10px 15px',
            backgroundColor: '#6c757d',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          ‚Üê Voltar para Turmas
        </button>

        <div style={{
          backgroundColor: '#f8d7da',
          color: '#721c24',
          padding: '15px',
          border: '1px solid #f5c6cb',
          borderRadius: '5px',
          marginBottom: '20px'
        }}>
          <strong>Erro:</strong> {error}
          <br />
          <small>Certifique-se de que sua API est√° rodando em http://localhost:8081</small>
        </div>
        <button
          onClick={() => window.location.reload()}
          style={{
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          Tentar Novamente
        </button>
      </div>
    );
  }

  if (!turma) {
    return (
      <div style={{ padding: '20px' }}>
        <button
          onClick={() => navigate('/turmas')}
          style={{
            marginBottom: '20px',
            padding: '10px 15px',
            backgroundColor: '#6c757d',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          ‚Üê Voltar para Turmas
        </button>
        <div style={{ textAlign: 'center', padding: '40px', color: '#6c757d' }}>
          Turma n√£o encontrada
        </div>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <button
        onClick={() => navigate('/turmas')}
        style={{
          marginBottom: '20px',
          padding: '10px 15px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          transition: 'background-color 0.2s'
        }}
        onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#0056b3'}
        onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#007bff'}
      >
        ‚Üê Voltar para Turmas
      </button>

      {isUsingMockData && (
        <div style={{
          backgroundColor: '#fff3cd',
          color: '#856404',
          padding: '15px',
          border: '1px solid #ffeaa7',
          borderRadius: '5px',
          marginBottom: '20px'
        }}>
          <strong>‚ö†Ô∏è Modo de Desenvolvimento:</strong> A API n√£o est√° dispon√≠vel. Exibindo dados de exemplo.
          <br />
          <small>Inicie sua API em http://localhost:8080 para ver os dados reais.</small>
        </div>
      )}

      <h1 style={{ marginBottom: '30px', color: '#343a40' }}>Detalhes da Turma</h1>

      <div style={{
        border: '1px solid #ddd',
        padding: '25px',
        borderRadius: '8px',
        backgroundColor: 'white',
        marginBottom: '30px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
      }}>
        <h2 style={{
          margin: '0 0 20px 0',
          color: '#007bff',
          fontSize: '1.8em'
        }}>
          {turma.disciplina.nome}
        </h2>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
          gap: '15px',
          marginBottom: '20px'
        }}>
          <div style={{
            padding: '15px',
            backgroundColor: '#f8f9fa',
