import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Turma } from '../types';
import { apiService } from '../services/api';

const TurmasPage: React.FC = () => {
  const [turmas, setTurmas] = useState<Turma[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTurmas = async () => {
      try {
        setLoading(true);
        const data = await apiService.getTurmas();
        setTurmas(data);
      } catch (err) {
        setError('Erro ao carregar turmas');
        console.error('Erro ao buscar turmas:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchTurmas();
  }, []);

  const handleTurmaClick = (turmaId: number) => {
    navigate(`/turma/${turmaId}`);
  };

  if (loading) return <div>Carregando...</div>;
  if (error) return <div>Erro: {error}</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Lista de Turmas</h1>
      <div style={{ display: 'grid', gap: '10px' }}>
        {turmas.map((turma) => (
          <div
            key={turma.id}
            onClick={() => handleTurmaClick(turma.id)}
            style={{
              border: '1px solid #ddd',
              padding: '15px',
              borderRadius: '5px',
              backgroundColor: '#f9f9f9',
              cursor: 'pointer',
              transition: 'background-color 0.2s'
            }}
            onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#e9e9e9'}
            onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#f9f9f9'}
          >
            <h3>{turma.disciplina.nome}</h3>
            <p><strong>Ano:</strong> {turma.ano}</p>
            <p><strong>Período:</strong> {turma.periodo}</p>
            <p><strong>Professor:</strong> {turma.professor.nome}</p>
            <p><strong>Código da Disciplina:</strong> {turma.disciplina.codigo}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TurmasPage;
