import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navigation: React.FC = () => {
  const location = useLocation();

  const navStyle = {
    backgroundColor: '#343a40',
    padding: '15px',
    marginBottom: '20px'
  };

  const linkStyle = {
    color: 'white',
    textDecoration: 'none',
    marginRight: '20px',
    padding: '10px 15px',
    borderRadius: '5px',
    backgroundColor: 'transparent',
    transition: 'background-color 0.2s'
  };

  const activeLinkStyle = {
    ...linkStyle,
    backgroundColor: '#007bff'
  };

  return (
    <nav style={navStyle}>
      <Link
        to="/alunos"
        style={location.pathname === '/alunos' ? activeLinkStyle : linkStyle}
      >
        Alunos
      </Link>
      <Link
        to="/turmas"
        style={location.pathname === '/turmas' ? activeLinkStyle : linkStyle}
      >
        Turmas
      </Link>
    </nav>
  );
};

export default Navigation;
